import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { createProductReview } from "../actions/productActions";
import { PRODUCT_CREATE_REVIEW_RESET } from "../constants/productConstants";
import Rating from "./Rating";

const ProductMetadata = ({product}) => {
//   const { id } = useParams();
  const dispatch = useDispatch();
//   const productDetails = useSelector((state) => state.productDetails);
//   const { loading, error, product } = productDetails;

//   const userLogin = useSelector((state) => state.userLogin);
//   const { userInfo } = userLogin;
//   console.log(product)
//   console.log(userInfo)

  return (
    <div className="py-12 px-4 md:px-6 2xl:px-0 2xl:container 2xl:mx-auto flex justify-center items-center">
      <div className="flex flex-col justify-start items-start w-full space-y-8">
        <div className="flex flex-col justify-start items-start ">
          <p className="text-3xl lg:text-4xl font-semibold leading-7 lg:leading-9 text-gray-800">
            Product Details
          </p>
        </div>
        <div className="productDetails">
        <div className="fullDescription">{product.description}</div>
        <div className="index-tableContainer">
           {product.map(([key, val])=>{
              return(<>
              <p>{key}</p>
              <br/>
              <p>{val}</p>
              </>)
           })}
   <div className="index-row">
      <div className="index-rowKey">Sport</div>
      <div className="index-rowValue">Running</div>
   </div>
   <div className="index-row">
      <div className="index-rowKey">Material</div>
      <div className="index-rowValue">Synthetic</div>
   </div>
   <div className="index-row">
      <div className="index-rowKey">Fastening</div>
      <div className="index-rowValue">Lace-Ups</div>
   </div>
   <div className="index-row">
      <div className="index-rowKey">Ankle Height</div>
      <div className="index-rowValue">Regular</div>
   </div>
   <div className="index-row">
      <div className="index-rowKey">Outsole Type</div>
      <div className="index-rowValue">Marking</div>
   </div>
   <div className="index-row">
      <div className="index-rowKey">Cleats</div>
      <div className="index-rowValue">No Cleats</div>
   </div>
   <div className="index-row">
      <div className="index-rowKey">Pronation for Running Shoes</div>
      <div className="index-rowValue">Neutral</div>
   </div>
   <div className="index-row">
      <div className="index-rowKey">Cushioning</div>
      <div className="index-rowValue">Low</div>
   </div>
</div>
</div>
      </div>
    </div>
  );
};

export default ProductMetadata;
