import React, { useEffect, useState } from "react";
import { MenuIcon, XIcon } from "@heroicons/react/outline";
import Sidebar from "../components/Sidebar";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import { listUsers } from "../actions/userActions";
import { Link, useNavigate } from "react-router-dom";
import {
  createProducts,
  deleteProducts,
  listProducts,
} from "../actions/productActions";
import { PRODUCT_CREATE_RESET } from "../constants/productConstants";

const AdminProductList = () => {
  const [openSidebar, setOpenSidebar] = useState(false);
  const navigate = useNavigate();

  const dispatch = useDispatch();
  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;

  const productDelete = useSelector((state) => state.productDelete);
  const {
    loading: loadingDelete,
    error: errorDelete,
    success: successDelete,
  } = productDelete;

  const productCreate = useSelector((state) => state.productCreate);
  const {
    loading: loadingCreate,
    error: errorCreate,
    success: successCreate,
    product: createdProduct,
  } = productCreate;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    dispatch({ type: PRODUCT_CREATE_RESET });
    setTimeout(() => {
      if (!userInfo.isAdmin) {
        navigate("/");
      }
    }, 5000);
    if (successCreate) {
      navigate(`/admin/product/${createdProduct._id}/edit`);
    } else {
      dispatch(listProducts());
    }
  }, [dispatch, userInfo, successDelete, successCreate, createdProduct]);

  const deleteHandler = (id) => {
    if (window.confirm("Are you sure")) {
      // delete products
      dispatch(deleteProducts(id));
    }
  };

  const createProductHandler = () => {
    dispatch(createProducts());
  };

  return (
    <div className="flex flex-row min-h-screen bg-gray-100 text-gray-800 md:overflow-x-hidden">
      <Sidebar openSidebar={openSidebar} />
      <main className="main flex flex-col flex-grow -ml-64 md:ml-0 transition-all duration-150 ease-in pl-64">
        <header className="header bg-white shadow py-4 px-4">
          <div className="header-content flex items-center flex-row">
            <div>
              <a href className="flex flex-row items-center">
                <img
                  src={`https://avatars.dicebear.com/api/initials/${userInfo.name}.svg`}
                  alt
                  className="h-10 w-10 bg-gray-200 border rounded-full"
                />
                <span className="flex flex-col ml-2">
                  <span className=" w-60 font-semibold tracking-wide leading-none">
                    {userInfo.name}
                  </span>
                  <span className=" w-30 text-gray-500 text-xs leading-none mt-1">
                    {userInfo.email}
                  </span>
                </span>
              </a>
            </div>
            <div className="flex ml-auto">
              {!openSidebar ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="text-black w-8 h-8 md:hidden"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  onClick={() => setOpenSidebar(!openSidebar)}
                >
                  <path
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    strokeWidth="2"
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="text-black w-8 h-8 md:hidden"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  onClick={() => setOpenSidebar(!openSidebar)}
                >
                  <path
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    strokeWidth="2"
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              )}
            </div>
          </div>
        </header>
        <div className="main-content flex flex-col flex-grow p-4">
          <div className="flex justify-between md:mx-10 mb-4 items-center">
            <h1 className="font-bold text-2xl text-gray-700 mb-6">
              Product List
            </h1>
            <button
              type="button"
              onClick={createProductHandler}
              className="text-white bg-gradient-to-br from-purple-600 to-blue-500 hover:bg-gradient-to-bl focus:ring-4 focus:ring-blue-300 dark:focus:ring-blue-800 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-2 mb-2"
            >
              + Create Product
            </button>
          </div>
          {/* table */}
          {loadingDelete && <p>loading....</p>}
          {errorDelete && <p>{errorDelete}</p>}

          {loadingCreate && <p>loading....</p>}
          {errorCreate && <p>{errorCreate}</p>}
          {loading ? (
            <Loader />
          ) : error ? (
            <p>{error}</p>
          ) : (
            <div className="flex flex-col w-[90vw] md:w-full overflow-x-scroll overflow-y-hidden md:overflow-y-auto">
              <div className="-my-2 overflow-x-auto sm:-mx-6 lg:-mx-8">
                <div className="py-2 align-middle inline-block min-w-full sm:px-6 lg:px-8">
                  <div className="shadow  border-b border-gray-200 sm:rounded-lg">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Product Id
                          </th>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Product Info
                          </th>
                          <th
                            scope="col"
                            className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                          >
                            Price
                          </th>
                          <th scope="col" className="relative px-6 py-3">
                            <span className="sr-only">Edit</span>
                          </th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {products.map((product) => (
                          <tr key={product._id}>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-gray-800">
                                {product._id}
                              </span>
                            </td>

                            <td className=" px-6 py-4 whitespace-nowrap">
                              <div className="flex  items-center">
                                <div className="">
                                  <p className="text-sm font-medium text-gray-900 ">
                                    {product.name}
                                  </p>
                                  <div className="text-sm text-gray-500">
                                    {product.category}
                                  </div>
                                  <div className="text-sm text-gray-500">
                                    {product.brand}
                                  </div>
                                </div>
                              </div>
                            </td>

                            <td className="px-6 py-4 whitespace-nowrap">
                              <div className="text-sm font-medium text-gray-900">
                                ${product.price}
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium flex space-x-2">
                              <Link
                                to={`/admin/product/${product._id}/edit`}
                                className="text-indigo-600 hover:text-indigo-900"
                              >
                                Edit
                              </Link>
                              <a
                                href="#"
                                className="text-red-600 hover:text-red-900"
                                onClick={() => deleteHandler(product._id)}
                              >
                                Delete
                              </a>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
